import React, { useState, useEffect } from 'react';
import { useUserContext } from '../context/UserContext';

const Orders = () => {
    const { user } = useUserContext();
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/api/getAllOrders`);
            if (response.ok) {
                const data = await response.json();
                setOrders(data);
            } else {
                console.error('Failed to fetch orders');
            }
        } catch (error) {
            console.error('An error occurred during order data fetch:', error);
        }
    };

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Orders Page</h2>
            <div className="table-responsive">
                <table className="table table-bordered">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>User ID</th>
                        <th>User Order</th>
                        <th>Price</th>
                    </tr>
                    </thead>
                    <tbody>
                    {orders.map(
                        (order) =>
                            user.id === order.user_id && (
                                <tr key={order.id}>
                                    <td>{order.id}</td>
                                    <td>{order.user_id}</td>
                                    <td>
                                        <ul>
                                            {JSON.parse(order.user_order).products.map((product) => (
                                                <li key={product.product_id}>
                                                    {product.product_name} - ${product.price} - Quantity:{' '}
                                                    {product.quantity}
                                                </li>
                                            ))}
                                        </ul>
                                    </td>
                                    <td>${order.price}</td>
                                </tr>
                            )
                    )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Orders;
