import React, { useState, useEffect } from 'react';
import './User.css'; // Import your CSS file for styling

const User = () => {
    const [users, setUsers] = useState([]);
    const [editingUser, setEditingUser] = useState(null);
    const [editValue, setEditValue] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/getAllUsers');
            if (response.ok) {
                const data = await response.json();
                setUsers(data);
            } else {
                console.error('Failed to fetch users');
            }
        } catch (error) {
            console.error('An error occurred during user data fetch:', error);
        }
    };

    const handleEditClick = (user, field) => {
        setEditingUser(user);
        setEditValue(user[field]);
    };
    
    return (
        <div className="user-container">
            <h2>User Page</h2>
            <table className="user-table">
                <thead>
                <tr>
                    <th>Email</th>
                    <th>Name</th>
                    <th>Role</th>
                    <th>Password</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {users.map((user) => (
                    <tr key={user.email}>
                        <td>{user.email}</td>
                        <td>{user.name}</td>
                        <td>{user.role}</td>
                        <td>{user.password}</td>
                        <td>
                            <td>
                                <button className="action-button">Edit</button>
                            </td>
                            <td>
                                <button className="action-button">Delete</button>
                            </td>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default User;
