import React, { useState, useEffect } from 'react';

const Orders = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        // Fetch orders data when the component mounts
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            // Make the API call to get orders data
            const response = await fetch('http://127.0.0.1:5000/api/getOrders?user_id=6');
            if (response.ok) {
                const data = await response.json();
                setOrders(data);
            } else {
                console.error('Failed to fetch orders');
            }
        } catch (error) {
            console.error('An error occurred during order data fetch:', error);
        }
    };

    return (
        <div>
            <h2>Orders Page</h2>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>User ID</th>
                    <th>User Order</th>
                    <th>Price</th>
                </tr>
                </thead>
                <tbody>
                {orders.map(order => (
                    <tr key={order.id}>
                        <td>{order.id}</td>
                        <td>{order.user_id}</td>
                        <td>
                            <ul>
                                {JSON.parse(order.user_order).products.map(product => (
                                    <li key={product.product_id}>
                                        {product.product_name} - ${product.price} - Quantity: {product.quantity}
                                    </li>
                                ))}
                            </ul>
                        </td>
                        <td>${order.price}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Orders;
