import React, {useState, useEffect} from 'react';
import './Inventory.css';
import {useUserContext} from '../context/UserContext';


const Inventory = () => {
    const {user} = useUserContext();
    const [products, setProducts] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalMode, setModalMode] = useState('add'); // 'add' or 'edit'
    const [modalData, setModalData] = useState({
        product_name: '',
        quantity: '',
        color: '',
        size: '',
        gender: '',
        price: '',
        category: '',
        history: '',
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/getAllProducts');
            if (response.ok) {
                const data = await response.json();
                setProducts(data.products);
            } else {
                console.error('Failed to fetch products');
            }
        } catch (error) {
            console.error('An error occurred during product data fetch:', error);
        }
    };

    const handleModalOpen = (mode, data = {}) => {
        console.log('open')
        setIsModalOpen(true);
        setModalMode(mode);
        setModalData(data);
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
        setModalMode('add');
        setModalData({
            product_name: '',
            quantity: '',
            color: '',
            size: '',
            gender: '',
            price: '',
            category: '',
            history: '',
        });
    };

    const handleModalSave = async () => {
        try {
            const apiUrl =
                modalMode === 'add'
                    ? 'http://127.0.0.1:5000/api/insertData'
                    : `http://127.0.0.1:3000/api/updateProduct/${modalData.product_id}`;

            const payload =
                modalMode === 'add'
                    ? { ...modalData, history: '' } // Set history to an empty string for 'add'
                    : { ...modalData }; // For 'edit', use the existing data without modifying history

            const response = await fetch(apiUrl, {
                method: modalMode === 'add' ? 'POST' : 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(payload),
            });

            if (response.ok) {
                console.log(`${modalMode === 'add' ? 'Added' : 'Updated'} successfully`);
                fetchProducts();
                setModalOpen(false);
            } else {
                console.error(`${modalMode === 'add' ? 'Add' : 'Update'} failed`);
            }
        } catch (error) {
            console.error(`An error occurred during ${modalMode === 'add' ? 'add' : 'edit'}:`, error);
        }
    };


    return (
        <div className="inventory-container">
            <div className="row">
                <div className="column">
                    <h2>Inventory Page</h2>
                </div>
                <div className="column">
                    <button onClick={() => handleModalOpen('add')}>
                        Add Product
                    </button>
                </div>
            </div>
            <table className="inventory-table">
                <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Quantity</th>
                    <th>Color</th>
                    <th>Size</th>
                    <th>Gender</th>
                    <th>Price</th>
                    <th>Category</th>
                    <th>History</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {products.map((product) => (
                    <tr key={product.product_id}>
                        <td>{product.product_name}</td>
                        <td>{product.quantity}</td>
                        <td>{product.color}</td>
                        <td>{product.size}</td>
                        <td>{product.gender}</td>
                        <td>{product.price}</td>
                        <td>{product.category}</td>
                        <td className="history">{product.history}</td>
                        <td>
                            {user.role !== "Admin" && <td>
                                <button className="action-button">Order</button>
                            </td>
                            }
                            {user.role === "Admin" && <td>
                                <button className="action-button">Edit</button>
                            </td>
                            }
                            {user.role === "Admin" && <td>
                                <button className="action-button">Delete</button>
                            </td>
                            }
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {isModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={handleModalClose}>
                            &times;
                        </span>
                        <h2>{modalMode === 'add' ? 'Add Product' : 'Edit Product'}</h2>
                        <form>
                            <label>
                                Product Name:
                                <input
                                    type="text"
                                    value={modalData.product_name}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            product_name: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            {/* Add other input fields similarly */}
                            <label>
                                Quantity:
                                <input
                                    type="number"
                                    value={modalData.quantity}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            quantity: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            <label>
                                Color:
                                <input
                                    type="text"
                                    value={modalData.color}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            color: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            <label>
                                Size:
                                <input
                                    type="text"
                                    value={modalData.size}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            size: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            <label>
                                Gender:
                                <input
                                    type="text"
                                    value={modalData.gender}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            gender: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            <label>
                                Price:
                                <input
                                    type="number"
                                    value={modalData.price}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            price: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            <label>
                                Category:
                                <input
                                    type="text"
                                    value={modalData.category}
                                    onChange={(e) =>
                                        setModalData({
                                            ...modalData,
                                            category: e.target.value,
                                        })
                                    }
                                />
                            </label>

                            {/* Add more input fields... */}

                            <button type="button" onClick={handleModalSave}>
                                Save
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Inventory;
